import React, { useState, useEffect } from 'react';
import Planet from '../Planet/planet.js';
import './RouteFinder.css';
import FlightInfo from '../Flight/FlightInfo.js';

function RouteFinder() {
  const [fromPlanet, setFromPlanet] = useState('');
  const [toPlanet, setToPlanet] = useState('');
  const [mode, setMode] = useState('selecting');
  const [routes, setRouteInfo] = useState([]);
  const [displayLimit, setDisplayLimit] = useState(5);
  const apiURL = 'http://localhost:5299';

  useEffect(() => {
    if (fromPlanet && toPlanet) {
      setMode('selected');
    } else if (fromPlanet) {
      setMode('deselected');
    } else {
      setMode('selecting');
    }
  }, [fromPlanet, toPlanet]); // Run the effect whenever the fromPlanet or the toPlanet changes

  // A callback function that updates the fromPlanet and the toPlanet values based on the mode and the planet name
  const handlePlanetClick = (planet) => {
    if (mode === 'selecting') {
      setFromPlanet(planet);
    } else if (mode === 'deselected') {
      setToPlanet(planet);
    } else if (mode === 'selected') {
      setMode('deselected');
      return;
    }
  };



  const validateForm = (event) => {
    event.preventDefault();
      if ((fromPlanet === '' || toPlanet === '') && event.target.value === 'submit') {
        alert('Please select a planet for both Origin and Destination.');
        return;
      }

      if (fromPlanet === toPlanet && fromPlanet !== '' && toPlanet !== '') {
        alert('From and To locations cannot be the same planet.');
        setToPlanet('');
        return;
      }

    
  };

  const handleClearClick = () => {
    setFromPlanet('');
    setToPlanet('');
  };

  const handleSearch = () => {
    searchRoutes(fromPlanet, toPlanet);
  };

  const searchRoutes = (fromPlanet, toPlanet) => {
    const url = apiURL + `/api/FlightFinder?startLoc=${fromPlanet}&endLoc=${toPlanet}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setRouteInfo(data);
        setDisplayLimit(5);
        console.log(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });

  };

  function sortRoutesByPrice(routes) {
    routes.forEach(route => {
      var routeCost = 0;

      for (var i = 0; i < route.length; i++) {
        routeCost += route[i]['price'];
      }

      route.totalCost = Math.round(routeCost * 100) / 100;
    });

    routes.sort((a, b) => a.totalCost - b.totalCost);

    return routes;
  }

  function sortRoutesByDuration(routes) {
    routes.forEach(route => {
      const originDepartureTime = new Date(route[0].departureTime);
      const destinationArrivalTime = new Date(route[route.length - 1].arrivalTime);

      var routeDurationMilliseconds = destinationArrivalTime - originDepartureTime;
      var routeDurationDays = Math.floor(routeDurationMilliseconds / (1000 * 60 * 60 * 24));

      route.totalDuration = routeDurationDays;
    });

    routes.sort((a, b) => a.totalDuration - b.totalDuration);

    return routes;
  }

  function sortRoutesByDistance(routes) {
    routes.forEach(route => {
      var routeDistance = 0;
      for (var i = 0; i < route.length; i++) {
        routeDistance += route[i].distance;
      }
      route.totalDistance = routeDistance;
    });

    routes.sort((a, b) => a.totalDistance - b.totalDistance);

    return routes;
  }

  const handleSortByPrice = () => {
    const sortedRoutes = sortRoutesByPrice([...routes]);
    setRouteInfo(sortedRoutes);
  };

  const handleSortByDuration = () => {
    const sortedRoutes = sortRoutesByDuration([...routes]);
    setRouteInfo(sortedRoutes);
  };

  const handleSortByDistance = () => {
    const sortedRoutes = sortRoutesByDistance([...routes]);
    setRouteInfo(sortedRoutes);
  }

  const showMoreRoutes = () => {
    setDisplayLimit(displayLimit + 5);
  }

  return (
    <div className="route-finder-container">
      <div className={routes.length > 0 ? "searchbox-with-results" : "searchbox"}>
        <h1>Select an Origin and Destination</h1>
        <form onSubmit={validateForm}>
          <div className="planet-container">
            <Planet
              planet="Mercury"
              onClick={() => handlePlanetClick("Mercury")}
            />
            <Planet
              planet="Venus"
              onClick={() => handlePlanetClick("Venus")}
            />
            <Planet
              planet="Earth"
              onClick={() => handlePlanetClick("Earth")}
            />
            <Planet
              planet="Mars"
              onClick={() => handlePlanetClick("Mars")}
            />
            <Planet
              planet="Jupiter"
              onClick={() => handlePlanetClick("Jupiter")}
            />
            <Planet
              planet="Saturn"
              onClick={() => handlePlanetClick("Saturn")}
            />
            <Planet
              planet="Uranus"
              onClick={() => handlePlanetClick("Uranus")}
            />
            <Planet
              planet="Neptune"
              onClick={() => handlePlanetClick("Neptune")}
            />
          </div>

          <div className="selected-planets">
            <p>Origin: {fromPlanet || ''}</p>
            <p>Destination: {toPlanet || ''}</p>
          </div>
          <div className="buttons">
            <span className="search-button" type="submit" onClick={() => handleSearch()}>Search</span>
            <span className="search-button" onClick={() => handleClearClick()}> Clear</span>
          </div>
        </form>
      </div >
    </div >
  );
}

export default RouteFinder;
