import React, { useState, useEffect } from 'react';
import Planet from './Planet/planet.js';

// A React component that renders the search routes form and the results
function RouteFinder() {
  const [fromPlanet, setFromPlanet] = useState('');
  const [toPlanet, setToPlanet] = useState('');
  const [mode, setMode] = useState('selecting'); // A state variable to store the mode
  const [routes, setRouteInfo] = useState([]);
  const [displayLimit, setDisplayLimit] = useState(5);

  // A useEffect hook to update the mode based on the fromPlanet and the toPlanet values
  useEffect(() => {
    if (fromPlanet && toPlanet) {
      // If both planets are selected, set the mode to "selected"
      setMode('selected');
    } else if (fromPlanet) {
      // If only the fromPlanet is selected, set the mode to "deselected"
      setMode('deselected');
    } else {
      // If no planet is selected, set the mode to "selecting"
      setMode('selecting');
    }
  }, [fromPlanet, toPlanet]); // Run the effect whenever the fromPlanet or the toPlanet changes

  // A callback function that updates the fromPlanet and the toPlanet values based on the mode and the planet name
  const handlePlanetClick = (planet) => {
    if (mode === 'selecting') {
      // If the mode is "selecting", set the fromPlanet to the clicked planet
      setFromPlanet(planet);
    } else if (mode === 'deselected') {
      // If the mode is "deselected", set the toPlanet to the clicked planet
      setToPlanet(planet);
    } else if (mode === 'selected') {
      // If the mode is "selected", reset both planets and set the mode to "selecting"
      setFromPlanet('');
      setToPlanet('');
      setMode('selecting');
    }
  };

  const validateForm = (event) => {
    event.preventDefault();

    if (fromPlanet === '' || toPlanet === '') {
      alert('Please select a planet for both Origin and Destination.');
      return;
    }

    if (fromPlanet === toPlanet) {
      alert('From and To locations cannot be the same planet.');
      return;
    }

    searchRoutes();
  };

  const searchRoutes = () => {
    const apiURL = process.env.REACT_APP_BACKEND_API_URL;
    const url = apiURL + `/api/FlightFinder?startLoc=${fromPlanet}&endLoc=${toPlanet}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setRouteInfo(data);
        setDisplayLimit(5);
        console.log(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  // Return the JSX code for the component
  return (
    <div className="route-finder-container">
      <h2>Search Routes</h2>
      <form onSubmit={validateForm}>
        <div className="planets-container">
          {/* Pass the handlePlanetClick function as a prop to the Planet component */}
          <Planet planet="Mercury" onClick={handlePlanetClick} />
          <Planet planet="Venus" onClick={handlePlanetClick} />
          <Planet planet="Earth" onClick={handlePlanetClick} />
          <Planet planet="Mars" onClick={handlePlanetClick} />
          <Planet planet="Jupiter" onClick={handlePlanetClick} />
          <Planet planet="Saturn" onClick={handlePlanetClick} />
          <Planet planet="Uranus" onClick={handlePlanetClick} />
          <Planet planet="Neptune" onClick={handlePlanetClick} />
        </div>
        <div className="selected-planets">
          <p>Origin: {fromPlanet || 'None'}</p>
          <p>Destination: {toPlanet || 'None'}</p>
        </div>
        <button type="submit">Search</button>
      </form>
      <div className="routes-container">
        {/* Render the routes and the results */}
      </div>
    </div>
  );
}

export default RouteFinder;
