import React, { useState, useEffect } from 'react';
import Planet from '../Planet/planet.js';
import './RouteFinder.css';
import FlightInfo from '../Flight/FlightInfo.js';

function RouteFinder() {
  const [fromPlanet, setFromPlanet] = useState('');
  const [toPlanet, setToPlanet] = useState('');
  const [mode, setMode] = useState('selecting');
  const [routeInfo, setRouteInfo] = useState(null);
  const [displayFlightInfo, setDisplayFlightInfo] = useState(false);
  const [displayLimit, setDisplayLimit] = useState(5);
  const apiURL = 'http://localhost:5299';

  useEffect(() => {
    if (fromPlanet && toPlanet) {
      setMode('selected');
    } else if (fromPlanet) {
      setMode('deselected');
    } else {
      setMode('selecting');
    }
  }, [fromPlanet, toPlanet]); // Run the effect whenever the fromPlanet or the toPlanet changes

  // A callback function that updates the fromPlanet and the toPlanet values based on the mode and the planet name
  const handlePlanetClick = (planet) => {
    if (mode === 'selecting') {
      setFromPlanet(planet);
    } else if (mode === 'deselected') {
      setToPlanet(planet);
    } else if (mode === 'selected') {
      return;
    }
  };


  const validateForm = (event) => {
    event.preventDefault();


    if ((fromPlanet === '' || toPlanet === '') && event.target.value === 'submit') {
      alert('Please select a planet for both Origin and Destination.');
      return;
    }

    if (fromPlanet === toPlanet && fromPlanet !== '' && toPlanet !== '') {
      alert('From and To locations cannot be the same planet.');
      setToPlanet('');
      return;
    }

    searchRoutes();

  };

  const handleClearClick = (id) => {
    setFromPlanet('');
    setToPlanet('');
  };

  const handleSearch = () => {
    searchRoutes(fromPlanet, toPlanet);
  };

const searchRoutes = (fromPlanet, toPlanet) => {
  const url = apiURL + `/api/FlightFinder?startLoc=${fromPlanet}&endLoc=${toPlanet}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setRouteInfo(data);
        setDisplayLimit(5);
        console.log(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });

};

const showMoreRoutes = () => {
  setDisplayLimit(displayLimit + 5);
};

const handleSortByPrice = () => {
  const sortedRoutes = routeInfo.sort((a, b) => a.price - b.price);
  setRouteInfo(sortedRoutes);
};

const handleSortByDuration = () => {
  const sortedRoutes = [...routeInfo].sort((a, b) => a.duration - b.duration);
  setRouteInfo(sortedRoutes);
};

// Define the handleSortByDistance function that will sort the routeInfo by distance
const handleSortByDistance = () => {
  const sortedRoutes = [...routeInfo].sort((a, b) => a.distance - b.distance);
  setRouteInfo(sortedRoutes);
};

// Add a button to toggle the displayFlightInfo state
const handleToggleFlightInfo = () => {
  setDisplayFlightInfo(!displayFlightInfo);
};

  return (
    <div className="route-finder-container">
      <h1>Select an Origin and Destination</h1>
      <form onSubmit={validateForm}>
        <div className="planet-container">
          <Planet
            planet="Mercury"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Venus"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Earth"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Mars"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Jupiter"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Saturn"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Uranus"
            onClick={handlePlanetClick}
          />
          <Planet
            planet="Neptune"
            onClick={handlePlanetClick}
          />
        </div>
        <div className="selected-planets">
          <p>Origin: {fromPlanet || ''}</p>
          <p>Destination: {toPlanet || ''}</p>
        </div>
        <div className="buttons">
          <span className="search-button" type="submit" onClick={() => handleSearch()}>Search</span>
          <span className="search-button" onClick={() => handleClearClick()}> Clear</span>
        </div>
        <div className="flight_info_wrapper">
          {displayFlightInfo && routeInfo && <FlightInfo route={routeInfo[0]} />}
        </div>
        <div className="route_info_wrapper">
          {!displayFlightInfo && routeInfo && routeInfo.slice(0, displayLimit).map((route, index) => (
            <RouteInfoComponent key={index} routeInfo={route} />
          ))}
        </div>
        <div className="sort_and_show_more_wrapper">
            <div className="sort_routes_wrapper">
              <button className={"sort_button"} onClick={() => handleSortByPrice()}>Price ↓</button>
              <button className={"sort_button"} onClick={() => handleSortByDuration()}>Duration ↓</button>
              <button className={"sort_button"} onClick={() => handleSortByDistance()}>Distance ↓</button>
            </div>
            {routes.length > displayLimit && (
              <div className="show_more_button_container">
                <button className="show_more_routes_button" onClick={() => showMoreRoutes()}>Show More Routes</button>
              </div>
            )}
          </div>
      </form>
    </div>
  );
}

export default RouteFinder;
