import React, { useState, useEffect } from 'react';
import Planet from '../Planet/planet.js';
import './RouteFinder.css';

function RouteFinder() {
  const [selectedPlanet, setSelectedPlanet] = useState('');
  const [fromPlanet, setFromPlanet] = useState('');
  const [toPlanet, setToPlanet] = useState('');
  const [mode, setMode] = useState('selecting');
  const [routes, setRouteInfo] = useState([]);
  const [displayLimit, setDisplayLimit] = useState(5);

  useEffect(() => {
    if (fromPlanet && toPlanet) {
      setMode('selected');
    } else if (fromPlanet) {
      setMode('deselected');
    } else {
      setMode('selecting');
    }
  }, [fromPlanet, toPlanet]); 

  const handlePlanetClick = (planet) => {
    if (mode === 'selecting') {
      setFromPlanet(planet);
    } else if (mode === 'deselected') {
      setToPlanet(planet);
    } else if (mode === 'selected') {
      return;
    }
  };


  const validateForm = (event) => {
    event.preventDefault();

    
      if ((fromPlanet === '' || toPlanet === '') && event.target.value === 'submit') {
        alert('Please select a planet for both Origin and Destination.');
        return;
      }

      if (fromPlanet === toPlanet && fromPlanet !== '' && toPlanet !== '') {
        alert('From and To locations cannot be the same planet.');
        setToPlanet('');
        return;
      }

      searchRoutes();
    
  };

  const handleClearClick = (id) => {
      setFromPlanet('');
      setToPlanet('');
  };


  const searchRoutes = () => {
    const apiURL = process.env.REACT_APP_BACKEND_API_URL;
    const url = apiURL + `/api/FlightFinder?startLoc=${fromPlanet.toLowerCase()}&endLoc=${toPlanet.toLowerCase()}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setRouteInfo(data);
        setDisplayLimit(5);
        console.log(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };


  return (
    <div className="route-finder-container">
      <h1>Select an Origin and Destination</h1>
      <form onSubmit={validateForm}>
        <div className="planet-container">
          <Planet
            planet="Mercury"
            onClick={handlePlanetClick}
            selectedPlanet={selectedPlanet}
            fromPlanet={fromPlanet}
            toPlanet={toPlanet}
          />
          {/* Add more Planet components for other planets */}
        </div>
        <div className="selected-planets">
          <p>Origin: {fromPlanet || ''}</p>
          <p>Destination: {toPlanet || ''}</p>
        </div>
        <div className="buttons">
          <span className="search-button" type="submit" onClick={searchRoutes}>Search</span>
          <span
            className="search-button"
            onClick={() => handleClearClick('to')}> Clear
          </span>
        </div>
      </form>
    </div>
  );
}

export default RouteFinder;
