function RouteFinder() {
  // ... the rest of your code

  // A helper function that returns the appropriate class name for a planet
  const getPlanetClass = (planet) => {
    if (mode === 'selecting') {
      return '';
    } else if (mode === 'deselected') {
      if (planet === fromPlanet) {
        return 'planet-from';
      } else {
        return '';
      }
    } else if (mode === 'selected') {
      if (planet === fromPlanet) {
        return 'planet-from planet-selected';
      } else if (planet === toPlanet) {
        return 'planet-to planet-selected';
      } else {
        return 'planet-selected';
      }
    }
  };

  return (
    <div className="route-finder-container">
      <h2>Search Routes</h2>
      <form onSubmit={validateForm}>
        <div className="planet-container">
          <Planet
            planet="Mercury"
            onClick={handlePlanetClick}
            className={getPlanetClass('Mercury')}
          />
          <Planet
            planet="Venus"
            onClick={handlePlanetClick}
            className={getPlanetClass('Venus')}
          />
          <Planet
            planet="Earth"
            onClick={handlePlanetClick}
            className={getPlanetClass('Earth')}
          />
          <Planet
            planet="Mars"
            onClick={handlePlanetClick}
            className={getPlanetClass('Mars')}
          />
          <Planet
            planet="Jupiter"
            onClick={handlePlanetClick}
            className={getPlanetClass('Jupiter')}
          />
          <Planet
            planet="Saturn"
            onClick={handlePlanetClick}
            className={getPlanetClass('Saturn')}
          />
          <Planet
            planet="Uranus"
            onClick={handlePlanetClick}
            className={getPlanetClass('Uranus')}
          />
          <Planet
            planet="Neptune"
            onClick={handlePlanetClick}
            className={getPlanetClass('Neptune')}
          />
        </div>
        <div className="selected-planets">
          <p>Origin: {fromPlanet || 'None'}</p>
          <p>Destination: {toPlanet || 'None'}</p>
        </div>
        <button type="submit">Search</button>
      </form>
    </div>
  );
}
